import { Component } from '@angular/core';
import { MousepositionService } from './service/mouseposition.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-character',
  templateUrl: './character.component.html',
  styleUrls: ['./character.component.scss']
})
export class CharacterComponent {

  public mousePositionX: number;
  public mousePositionY: number;

  constructor(private mousepositionService:MousepositionService){
    this.mousePositionX = mousepositionService.getMousePositionX();
    this.mousePositionY = mousepositionService.getMousePositionY();
  }


}
<div id="character">
    <div id="leftear">

    </div>
    <div id="head">
<div id="eyeleft">
    <div id="eyebuble"></div>
</div>
<div id="eyeright">
    <div id="eyebuble" [style.transform]="'translateX(' + 10 + '%) translateY(' + 40 + '%)'"></div>
</div>
<div id="mouth"></div>
    </div>
    <div id="rightear">

    </div>

</div>
