import { Injectable,Inject } from '@angular/core';
import { DOCUMENT } from '@angular/common';
import { fromEvent, Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class MousepositionService {
  private readonly mousePosition$: Observable<[number,number]>; //OBSERVABLE HOLDS THE MOUSE POSITION
  private positionX: number = 0;
  private positionY: number = 0;

  constructor(@Inject(DOCUMENT) private document:Document) { 
    this.mousePosition$ = fromEvent<MouseEvent>(this.document,'mousemove').pipe(
      map(event =>[event.clientX, event.clientY]) //MAPPING THE EVENT ON THE DOCUMENT INTO THE MOUSE POSITION
    )

  }

  getMousePosition(): Array<number> {
    this.mousePosition$.subscribe(([x,y]) => {
      this.positionX = (x/window.innerWidth)*100;
      this.positionY = (y/window.innerHeight)*100;
    })
    return [this.positionX,this.positionY]; //MOUSE POSITION GETTER
  }
}
