import { Component } from '@angular/core';
import { MousepositionService } from './service/mouseposition.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-character',
  templateUrl: './character.component.html',
  styleUrls: ['./character.component.scss']
})
export class CharacterComponent {


  constructor(public mousepositionService:MousepositionService){
  }


}
<div id="character">
    <div id="leftear">

    </div>
    <div id="head">
<div id="eyeleft">
    <div id="eyebuble" [ngStyle]="{'transform': 'translate(' + mousepositionService.getMousePositionX() + '%, ' + mousepositionService.getMousePositionY() + '%)'}"></div>
</div>
<div id="eyeright">
    <div id="eyebuble" [ngStyle]="{'transform': 'translate(' + mousepositionService.getMousePositionX() + '%, ' + mousepositionService.getMousePositionY() + '%)'}"></div>
</div>
<div id="mouth"></div>
    </div>
    <div id="rightear">

    </div>

</div>
